import { useNavigate } from 'react-router-dom';
import EmojiPicker from 'emoji-picker-react';
import './chatPage.css'
import { useState } from 'react';

const ChatPage = () => {
    const navigate = useNavigate();

    const [text, setText] = useState('');
    const [emojiOpen, setEmojiOpen] = useState(false);

    const handleEmoji = (e) => {
        setText((prev) => prev + e.emoji);
        setEmojiOpen(false);
    }

    const logOut = () => {
        localStorage.setItem('jwt', null);
        navigate('/login', {replace: true});
    }

    return (
        <div className='container'>
            <div className='list'>
                <div className='userInfo'>
                    <h2>Name1</h2>
                    <button className='button' onClick={logOut}>Выйти</button>
                </div>

                <div className='search'>
                    <input className='searchBar' type='text' placeholder='Поиск'/>
                    <button className='button'>Добавить</button>
                </div>

                <div className='chatList'>
                    <div className='chat'>
                        <h3>Name2</h3>
                        <p>textsef</p>
                    </div>
                    <div className='chat'>
                        <h3>Name2</h3>
                        <p>textrtshstgs</p>
                    </div>
                    <div className='chat'>
                        <h3>Name2</h3>
                        <p>te</p>
                    </div>
                </div>
            </div>

            <div className='messanger'>
                <div className="top">
                    <h2>Name2</h2>
                </div>

                <div className="messages">

                </div>

                <div className="bottom">
                    <input className='input' type="text" placeholder="Введите сообщение" onChange={e => setText(e.target.value)}/>
                    <div className='emoji' onClick={() => setEmojiOpen(prev => !prev)}>
                        👍
                        <EmojiPicker open={emojiOpen} onEmojiClick={handleEmoji}/>
                    </div>
                    <button className='button'>Отправить</button>
                </div>
            </div>
        </div>
    )
}

export {ChatPage}
