import EmojiPicker from "emoji-picker-react";
import { useEffect, useState } from "react";
import { useConnection } from '../../../../hook/useConnection';
import './messanger.css';
import { getMessages, sendMessage } from "../../../../hoc/Requests";

const Messanger = () => {
    const [text, setText] = useState('');
    const [emojiOpen, setEmojiOpen] = useState(false);
    const [messages, setMessages] = useState([]);
    const { currentChat, currentBuddy } = useConnection();

    const handleEmoji = (e) => {
        setText((prev) => prev + e.emoji);
        setEmojiOpen(false);
    }

    const handleSend = () => {
        if (text === '') return;

        sendMessage(currentChat, text);

    }

    useEffect(() => {
        if (currentChat) {
            getMessages(currentChat).then(res => {
                if (res.status === 200) {
                    setMessages(res.data);
                    console.log(res.data);
                }
            }).catch(err => console.log(err));
        }
    }, [currentChat])

    return (
        <div className='messanger'>
            <div className="top">
                <h2>{currentBuddy}</h2>
            </div>

            <div className="messages">
                {messages.map((message) => (
                    <div className="message">
                        <p>{message.text}</p>
                        <span></span>
                    </div>
                ))}
            </div>

            <div className="bottom">
                <input 
                    className='input'
                    type="text"
                    placeholder="Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ"
                    value={text}
                    onChange={e => setText(e.target.value)}
                />
                <div className='emoji' onClick={() => setEmojiOpen(prev => !prev)}>
                    ðŸ˜ƒ
                    <div className="emojiPicker">
                        <EmojiPicker open={emojiOpen} onEmojiClick={handleEmoji}/>
                    </div>
                </div>
                <button className='button' onClick={handleSend}>ÐžÑ‚Ð¿Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ</button>
            </div>
        </div>
    );
}

export {Messanger}
