import { useNavigate } from "react-router-dom";
import './list.css';
import { AddUser } from "./AddUser";
import { useState, useEffect, useCallback } from "react";
import { useConnection } from '../../../../hook/useConnection';
import { getChats } from "../../../../hoc/Requests";

const List = () => {
    const navigate = useNavigate();
    const { connection } = useConnection();

    const [addMode, setAddMode] = useState(false);
    const [chats] = useState([]);

    const logOut = () => {
        localStorage.setItem('jwt', null);
        localStorage.setItem('username', null)
        navigate('/login', {replace: true});
    }

    const updateChats = useCallback(() => {
        getChats().then(res => {
            while (chats.length > 0) {
                chats.pop();
            }

            res.data.map((chat) => (
                chats.push(chat)
            ));
        }).catch(err => console.log(err));
    }, [chats])

    useEffect(() => {
        updateChats();
    }, [updateChats])

    useEffect(() => {
        if (connection !== null) {
            connection.on('UpdateChatsMessage', (message) => {
                if (message === localStorage.getItem('username')) {
                    console.log(message);
                    window.location.reload();
                }
            });
        }
    }, [connection, updateChats])

    const handleSelect = (event) => {
        event.preventDefault();
    }
    
    return (
        <div className='list'>
            <div className='userInfo'>
                <h2>{localStorage.getItem('username')}</h2>
                <button className='button' onClick={logOut}>Выйти</button>
            </div>

            <div className='search'>
                <input className='searchBar' type='text' placeholder='Поиск'/>
                <button className='button' onClick={() => setAddMode((prev) => !prev)}>{addMode ? '-' : '+'}</button>
            </div>

            {addMode && <AddUser/>}

            <div className='chatList'>
                {chats.map((chat) => (
                    <div className='chat' key={chat.id} onClick={handleSelect}>
                        <h3>{chat.username}</h3>
                        <p>{chat.lastMessage}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export {List}
