import { HubConnectionBuilder } from "@microsoft/signalr";
import { createContext, useEffect, useState } from "react";

export const ConnectionContext = createContext(null);

export const ConnectionProvider = ({children}) => {
    const [connection, setConnection] = useState(null);
    
    useEffect(() => {
        async function con() {
            const connection = new HubConnectionBuilder()
            .withUrl("https://localhost:7253/notifyHub")
            .withAutomaticReconnect()
            .build();

            connection.on("UpdateMessage", (message) => {
                if (message === localStorage.getItem('username')) console.log(message)
            });

            setConnection(connection);
            try {
                await connection.start();
                connection.invoke('SendMessage', 'srgseg');
            } catch (error) {
                console.log(error);
            }
        }
        
        con();
    }, []);

    const value = {connection}

    return <ConnectionContext.Provider value={value}>
        {children}
    </ConnectionContext.Provider>
}
