import { createContext, useState } from "react";
import { access, authorize, register } from "./Requests";

export const AuthContext = createContext(null);

export const AuthProvider = ({children}) => {
    const [authorized, setAuthorized] = useState(false);
    const [error, setError] = useState(null);

    const reg = (username, password, cb) => {
        register(username, password).then(res => localStorage.setItem('jwt', res.data)).catch(err => console.log(err));
        cb();
    }
    const auth = (username, password, cb) => {
        authorize(username, password).then(res => {
            localStorage.setItem('jwt', res.data);
            cb();
        }).catch(err => {
            console.log(err);
        });
    }
    const logIn = () => {
        access().then(res => {
            if (res.status === 200) {
                setAuthorized(true);
            }
        }).catch(err => {
            setAuthorized(false);
            console.log(err);
        });
    }
    const logOut = (cb) => {
        localStorage.setItem('jwt', null);
        setAuthorized(false);
        cb();
    }

    const value = {authorized, error, reg, auth, logIn, logOut}

    return <AuthContext.Provider value={value}>
        {children}
    </AuthContext.Provider>
}
