import { faCheck, faTimes, faInfoCircle } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useEffect, useRef, useState } from 'react'
import { Link } from 'react-router-dom';

const LoginPage = () => {
    const userRef = useRef();
    const errorRef = useRef();

    const [username, setUsername] = useState('');
    const [userFocus, setUserFocus] = useState(false);

    const [password, setPassword] = useState('');
    const [passwordFocus, setPasswordFocus] = useState(false);

    const [errorMsg, setErrorMsg] = useState('');

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrorMsg('');
    }, [username, password])

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!v1 || !v2) {
            setErrorMsg("Недействительный ввод");
            return;
        }
        try {
            setSuccess(true);
            // setUsername('');
            // setPassword('');
            // setMatchPassword('');
        } catch (error) {
            // if (!error?.response) {
            //     setErrorMsg('Нет ответа от сервера');
            // } else if (error.response?.status === 409) {
            //     setErrorMsg('Имя занято');
            // } else {
            //     setErrorMsg('Регистрация не удалась')
            // }
            // errorRef.current.focus();
        }
    }

    return (
        <>
            <section>
                <p ref={errorRef} className={errorMsg ? "errormsg" : "offscreen"} aria-live="assertive">{errorMsg}</p>
                <h1>Вход</h1>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="username">
                        Имя:
                        <FontAwesomeIcon icon={faCheck} className={username ? "valid" : "hide"} />
                    </label>
                    <input
                        type="text"
                        id="username"
                        ref={userRef}
                        autoComplete="off"
                        onChange={(e) => setUsername(e.target.value)}
                        value={username}
                        required
                        onFocus={() => setUserFocus(true)}
                        onBlur={() => setUserFocus(false)}
                    />

                    <label htmlFor="password">
                        Пароль:
                        <FontAwesomeIcon icon={faCheck} className={password ? "valid" : "hide"} />
                    </label>
                    <input
                        type="password"
                        id="password"
                        onChange={(e) => setPassword(e.target.value)}
                        value={password}
                        required
                        onFocus={() => setPasswordFocus(true)}
                        onBlur={() => setPasswordFocus(false)}
                    />

                    <button disabled={!validName || !validPassword || !validMatch ? true : false}>Войти</button>
                </form>
                <p>
                    Еще нет аккаунта?<br />
                    <span className="line">
                        <Link to="/register">Зарегистрироваться</Link>
                    </span>
                </p>
            </section>
        </>
    )
}

export {LoginPage}
