import './addUser.css';
import { searchUser } from '../../../../hoc/Requests';
import { useState } from 'react';

const AddUser = () => {
    const [username, setUsername] = useState('');
    const [user, setUser] = useState(null);

    const handleSearch = (event) => {
        event.preventDefault();
        searchUser(username).then(setUser(username)).catch(err => {
            setUser(null);
            console.log(err)
        });
    }

    return (
        <div className="addUser">
            <form onSubmit={handleSearch}>
                <input type='text' placeholder='Введите имя' value={username} onChange={(e) => setUsername(e.target.value)}/>
                <button className='button'>Поиск</button>
            </form>
            { user && <div className='user'>
                <p>{username}</p>
                <button className='button'>Добавить</button>
            </div> }
        </div>
    );
}

export {AddUser}
