import { createContext, useRef } from "react";
import { access, authorize, register } from "./Requests";

export const AuthContext = createContext(null);

export const AuthProvider = ({children}) => {
    const code = useRef();

    const reg = (username, password) => {
        register(username, password).then(res => {
            localStorage.setItem('jwt', res.data)
            code.current(res.status);
        }).catch(err => {
            code.current(err.response.status)
        });
    }
    const auth = (username, password) => {
        authorize(username, password).then(res => {
            localStorage.setItem('jwt', res.data);
            code.current(res.status);
        }).catch(err => {
            localStorage.setItem('jwt', null);
            code.current(err.response.status);
        });
    }
    const logIn = () => {
        access().then(res => {
            code.current(res.status);
        }).catch(err => {
            code.current(err.response.status);
        });
    }
    const logOut = (cb) => {
        localStorage.setItem('jwt', null);
        cb();
    }

    const value = {code, reg, auth, logIn, logOut}

    return <AuthContext.Provider value={value}>
        {children}
    </AuthContext.Provider>
}
