import { faCheck } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useEffect, useRef, useState } from 'react'
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../hook/useAuth';

const LoginPage = () => {
    const navigate = useNavigate();

    const location = useLocation();
    const fromLink = location.state?.from?.pathname || "/"

    const userRef = useRef();
    const errorRef = useRef();

    const [username, setUsername] = useState('');

    const [password, setPassword] = useState('');

    const [errorMsg, setErrorMsg] = useState('');

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrorMsg('');
    }, [username, password])

    const {auth} = useAuth();

    const handleSubmit = (event) => {
        event.preventDefault();
        try {
            auth(username, password, () => navigate(fromLink, {replace: true}));
        } catch (error) {
            console.log(error);
            if (!error?.response) {
                setErrorMsg('Нет ответа от сервера');
            } else if (error.response?.status === 401) {
                setErrorMsg('Такого пользователя нет');
            } else {
                setErrorMsg('Авторизация не удалась')
            }
            errorRef.current.focus();
        }
    }

    return (
        <>
            <section>
                <h1>Вход</h1>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="username">
                        Имя:
                        <FontAwesomeIcon icon={faCheck} className={username ? "valid" : "hide"} />
                    </label>
                    <input
                        type="text"
                        id="username"
                        ref={userRef}
                        autoComplete="off"
                        onChange={(e) => setUsername(e.target.value)}
                        value={username}
                        required
                    />

                    <label htmlFor="password">
                        Пароль:
                        <FontAwesomeIcon icon={faCheck} className={password ? "valid" : "hide"} />
                    </label>
                    <input
                        type="password"
                        id="password"
                        onChange={(e) => setPassword(e.target.value)}
                        value={password}
                        required
                    />

                    <button disabled={username && password ? false : true}>Войти</button>
                </form>

                <p ref={errorRef} className={errorMsg ? "errormsg" : "offscreen"} aria-live="assertive">{errorMsg}</p>

                <p>
                    Еще нет аккаунта?<br />
                    <span className="line">
                        <Link to="/register">Зарегистрироваться</Link>
                    </span>
                </p>
            </section>
        </>
    )
}

export {LoginPage}
