import { Navigate, useLocation } from "react-router-dom"
import { useAuth } from "../hook/useAuth";

const RequireAuth = ({children}) => {
    const location = useLocation();
    const {logIn} = useAuth();

    logIn(() => {return <Navigate to='/login' state={{from: location.state}}/>});

    return children;
}

export {RequireAuth}
